/** @type {gdjs.TextEntryRuntimeObject} */
const object = objects[0];
const array = object.getString().replace(/\n/gm, "").trim().split(/\s+/);
object.setString("");

object.getVariables()
    .get("__Commands_")
    .getChild("Triggered")
    .getChild(array[0])
    .setBoolean(true);

gdjs.evtTools.network._objectToVariable(
    array.slice(1),
    object.getVariables()
        .get("__Commands_")
        .getChild("Args")
        .getChild(array[0])
);
